import { List, Card } from 'antd';
import Link from 'next/link'
import MyLayout from '../homeLayout/myLayout'

const { Meta } = Card;

const MyList = (props) => {
    console.log(props);
    const data = props.shows.data.list;
    return (
        <MyLayout>
            <List
                grid={{
                    gutter: 16,
                    xs: 1,
                    sm: 2,
                    md: 4,
                    lg: 4,
                    xl: 6,
                    xxl: 3,
                }}
                dataSource={data}
                renderItem={(item) => (
                    <List.Item>
                        <Link href={`/components/details/litsDetails?id=${item.accountId}`} as={`/components/details/litsDetails`} key={item.ironDir}>
                            <Card
                                hoverable
                                style={{ width: 240 }}
                                cover={<img alt="example" src={item.ironDir} />}
                            >
                                <Meta title={item.name} description={`${item.level}万等级`} />
                            </Card>
                        </Link>
                    </List.Item>
                )}
            />
            <style jsx>
                {
                    `.shifu-img{
                        width : 200px;
                        height : 200px;
                    }`
                }
            </style>
        </MyLayout>
    )
}

MyList.getInitialProps = async function () {
    const res = await fetch('https://worker-api.wanshifu.com/master/listinfo');
    const data = await res.json();
    return {
        shows: data
    }
}

export default MyList
