import { Descriptions } from 'antd';


const ListDetails = (props) => {
    console.log(props);
    return (
        <Descriptions title="User Info" layout="vertical">
            <Descriptions.Item label="UserName">{props.data.name}</Descriptions.Item>
            <Descriptions.Item label="星级">{`${props.data.level}`}</Descriptions.Item>
            <Descriptions.Item label="Address" span={2}>
                {props.data.crumbName}
            </Descriptions.Item>
            <Descriptions.Item label="">
                <img alt="example" src={props.data.portrait} />
            </Descriptions.Item>
        </Descriptions>
    )
}

ListDetails.getInitialProps = async function (context) {
    const { id } = context.query;
    const res = await fetch("https://worker-api.wanshifu.com/master/details", {
        method: "POST",
        headers: {
            'Content-Type': 'application/x-www-form-urlencoded'
        },
        body: `masterId=${id}`
    })
    const data = await res.json();
    // return {
    //     shows: data
    // }
    return { ...data };
}

export default ListDetails
