import { List, Card, Row, Col } from 'antd';
import Link from 'next/link'
import MyLayout from '../homeLayout/myLayout'

const { Meta } = Card;

const MyList = (props) => {
    const data = props.shows.data.list;
    console.log(data,'data?');
    return (
        <MyLayout>
            {data.map(it=>{
                return (
                    <Row type='flex' justify='space-around' align='middle'>
                        <Col span={12}>
                            <div style={{ height:120 }}>
                                <img src={it.ironDir} style={{ width:120,height:120 }} />
                                <div style={{ height:120 }}>
                                  <p>{item.name}</p>
                                </div>
                            </div>
                        </Col>
                    </Row>
                )
            })}
            <List
                grid={{
                    gutter: 16,
                    xs: 1,
                    sm: 2,
                    md: 4,
                    lg: 4,
                    xl: 6,
                    xxl: 3,
                }}
                dataSource={data}
                renderItem={(item) => (
                    <List.Item>
                        <Link href={`/components/details/litsDetails?id=${item.accountId}`} as={`/list/details`}>
                            <Card
                                hoverable
                                style={{ width: 240 }}
                                cover={<img alt="example" src={item.ironDir} />}
                            >
                                <Meta title={item.name} description={`${item.level}万等级`} />
                            </Card>
                        </Link>
                    </List.Item>
                )}
            />
            <style jsx>
                {
                    `.shifu-img{
                        width : 200px;
                        height : 200px;
                    }`
                }
            </style>
        </MyLayout>
    )
}

MyList.getInitialProps = async function () {
    const res = await fetch('https://worker-api.wanshifu.com/master/listinfo');
    const data = await res.json();
    return { shows: data }
}

export default MyList
