import { List, Card } from 'antd';
import MyLayout from '../homeLayout/myLayout'

const MyList = (props) => {
    console.log(props);
    const data = props.shows.data.list;
    return (
        <MyLayout>
            <List
                grid={{
                    gutter: 16,
                    xs: 1,
                    sm: 2,
                    md: 4,
                    lg: 4,
                    xl: 6,
                    xxl: 3,
                }}
                dataSource={data}
                renderItem={(item) => (
                    <List.Item>
                        <Card title={item.name}>
                            <img src={item.ironDir} alt='' className = 'shifu-img'/>
                        </Card>
                    </List.Item>
                )}
            />
            <style jsx>
                {
                    `.shifu-img{
                        width : 200px;
                        height : 200px;
                    }`
                }
            </style>
        </MyLayout>
    )
}

MyList.getInitialProps = async function () {
    const res = await fetch('https://worker-api.wanshifu.com/master/listinfo');
    const data = await res.json();
    return {
        shows: data
    }
}

export default MyList
